import React, { useState } from 'react';
import { FlatList } from 'react-native';
import SwgohItem from './ApiData';
import useSwgohApi from './hooks/apiSwgoh';

const ApiSwgoh = () => {
  const [filter, setFilter] = useState(null);
  const { apiSwgoh } = useSwgohApi();


  const filterData = (name = 'Boba Fett') => {
    // let name = 'admiral raddus';
    const filteredData = apiSwgoh.filter((item) => item.name.toLowerCase().includes(name.toLowerCase()));
    setFilter(filteredData);
  };

  const apiSwgohFilter = filter ? filterData : apiSwgoh;

  return (
    <FlatList
      data={apiSwgohFilter}
      keyExtractor={(item) => item.base_id}
      renderItem={({ item: data }) => <SwgohItem data={data} />}
    />
  );
};

export default ApiSwgoh;
